import React, {useState, useEffect} from 'react';
import './App.css';
import {Container, Row, Button, Nav, NavItem, NavLink, Popover, PopoverHeader, PopoverBody, ListGroup, ListGroupItem, ListGroupItemText} from 'reactstrap';
import Movie from './components/Movie';
 
function App() {

  /* INITIALISATION DES ETATS */
  const [popoverOpen, setPopoverOpen] = useState(false); //Popover
  const [moviesWishList, setMoviesWishList]=useState([]); //Wishlist
  const [moviesCount, setMoviesCount]= useState(0); //Compteur Wishlist
  const [movieList, setMovieList]= useState([]);

 

  /* EVENEMENT POPOVER */
  const toggle = () => setPopoverOpen(!popoverOpen);

  /* EVENEMENT AJOUT MOVIE WISHLIST */
  var handleClickAddMovie=(name, img)=>{
    setMoviesCount(moviesCount+1); //Incrémente le compteur de la Wishlist
    setMoviesWishList([...moviesWishList, {name: name, img: img}]);
  }

  /* EVENEMENT SUPPRESSION MOVIE WISHLIST */
  var handleClickDeleteMovie=(name)=>{
    console.log('click détécté!')
    setMoviesCount(moviesCount-1); //Décrémente le compteur de la Wishlist
    setMoviesWishList(moviesWishList.filter(object => object.name != name)); //HERE
  }


  /* CREATION DE LA WISHLIST */
  var cardWish=moviesWishList.map((movie, i) => {
    return(
      <ListGroupItem>
        <ListGroupItemText onClick={() => {handleClickDeleteMovie(movie.name)}}>
          <img width="25%" src={movie.img} alt={movie.name}/> {movie.name}
        </ListGroupItemText>
      </ListGroupItem>
    )
  })
  

  /* DONNEES MOVIES */
  var moviesData = [
    {name:"Star Wars : L'ascension de Skywalker", desc:"La conclusion de la saga Skywalker. De nouvelles légendes vont naître dans cette ...", img:"/starwars.jpg", note:6.7, vote:5},
    {name:"Maléfique : Le pouvoir du mal", desc: "Plusieurs années après avoir découvert pourquoi la plus célèbre méchante Disney avait un cœur ...", img:"/maleficent.jpg", note:8.2, vote:3},
    {name:"Jumanji: The Next Level", desc: "L’équipe est de retour, mais le jeu a changé. Alors qu’ils retournent dans Jumanji pour secourir ...", img:"/jumanji.jpg", note:4, vote:5},
    {name:"Once Upon a Time... in Hollywood", desc: "En 1969, Rick Dalton – star déclinante d'une série télévisée de western – et Cliff Booth ...", img:"/once_upon.jpg", note:6, vote:7},
    {name:"La Reine des neiges 2", desc: "Elsa, Anna, Kristoff, Olaf et Sven voyagent bien au-delà des portes d’Arendelle à la recherche de réponses ...", img:"/frozen.jpg", note:4.6, vote:3},
    {name:"Terminator: Dark Fate", desc: "De nos jours à Mexico. Dani Ramos, 21 ans, travaille sur une chaîne de montage dans une usine automobile...", img:"/terminator.jpg", note:6.1, vote:1},
  ]


  useEffect(()=>{
    async function loadData() {
      var rawResponse= await fetch('/new-movies')
      var response= await rawResponse.json();
      setMovieList(response)
      
    }
    loadData();
  }, [])

  console.log('movieList : ' + movieList[0])
  
  /* CREATION LISTE MOVIES */
  var dataMovies = response.map((movie, i) => {
    var result = moviesWishList.find(element => element.name == movie.name)
    var isSee = false
    if(result != undefined){
      isSee = true
    }
    return(<Movie key={i} movieName={movie.title} movieDesc={movie.desc} movieImg={'https://image.tmdb.org/t/p/w500/'+ movie.backdrop_path} globalRating={movie.popularity} globalCountRating={movie.vote_count} handleClickAddMovieParent={handleClickAddMovie} handleClickDeleteMovieParent={handleClickDeleteMovie} movieSee={isSee} />)
  })


  return (
    <div style={{backgroundColor:"#222f3e"}}>
      <Container>

        <Nav className="align-items-center">
          <span className="navbar-brand">
            <img src="./logo.png"width="30" height="30" className="d-inline-block align-top" alt="logo" />
          </span>
          <NavItem>
            <NavLink id='navName' style={{color:'white'}}>Last Releases</NavLink>
          </NavItem>
          <NavItem>
            <NavLink>
              <Button id="Popover1" type="button">{moviesCount} films 
              <div>
                <Popover placement="bottom" isOpen={popoverOpen} target="Popover1" toggle={toggle}>
                  <PopoverHeader>WishList</PopoverHeader>
                  <PopoverBody>
                    <ListGroup>
                      {cardWish}
                    </ListGroup>
                  </PopoverBody>
                </Popover>
              </div>
              </Button>
            </NavLink>
          </NavItem>
        </Nav>
      
        <Row>
          {movieList}
        </Row>

      </Container>
    </div>
  );
}

export default App;
